const select=document.querySelector(".select"),projectsList=document.querySelector(".projects-list"),allProjects=Array.from(document.querySelectorAll(".project"));function filterProjects(e){projectsList.innerHTML="";let t="all"===e?allProjects:allProjects.filter(t=>t.dataset.category===e);t.forEach(e=>projectsList.appendChild(e))}select.addEventListener("change",e=>{filterProjects(e.target.value)}),window.addEventListener("pageshow",function(e){(e.persisted||window.performance&&window.performance.getEntriesByType("navigation")[0]?.type==="back_forward")&&(select.value="all",filterProjects("all"))});